                    className="peer w-full bg-transparent border-b-2 border-slate-300 dark:border-slate-700 py-3 text-lg text-slate-900 dark:text-white focus:border-indigo-500 focus:outline-none transition-colors"
                    placeholder=" "
                  />
                  <label className="absolute left-0 -top-3.5 text-sm text-slate-500 transition-all peer-placeholder-shown:text-base peer-placeholder-shown:top-3 peer-focus:-top-3.5 peer-focus:text-sm peer-focus:text-indigo-500">Business Name</label>
