import React, { useState, useEffect } from 'react';
import { motion, useScroll, useSpring, AnimatePresence } from 'framer-motion';
import { Navbar } from './components/Navbar';
import { Hero } from './components/Hero';
import { About } from './components/About';
import { PainPoints } from './components/PainPoints';
import { CompetitiveEdge } from './components/CompetitiveEdge';
import { Projects } from './components/Projects';
import { LaunchPlan } from './components/LaunchPlan';
import { Testimonials } from './components/Testimonials';
import { Contact } from './components/Contact';
import { Footer } from './components/Footer';
import { MessageSquare } from 'lucide-react';

export default function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showFab, setShowFab] = useState(false);
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, { stiffness: 100, damping: 30 });

  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  useEffect(() => {
    const updateMousePosition = (e: MouseEvent) => setMousePosition({ x: e.clientX, y: e.clientY });
    window.addEventListener('mousemove', updateMousePosition);
    return () => window.removeEventListener('mousemove', updateMousePosition);
  }, []);

  useEffect(() => {
    setDarkMode(true); 
    const handleScroll = () => setShowFab(window.scrollY > 500);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="relative min-h-screen overflow-x-hidden bg-slate-50 dark:bg-[#030014]">
      <div className="fixed inset-0 opacity-[0.03] pointer-events-none z-[100] mix-blend-overlay bg-[url('https://grainy-gradients.vercel.app/noise.svg')]"></div>
      <div className="pointer-events-none fixed inset-0 z-0" style={{ background: `radial-gradient(600px circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(99, 102, 241, 0.07), transparent 40%)` }} />
      <motion.div className="fixed top-0 left-0 right-0 h-1 bg-gradient-to-r from-indigo-500 via-purple-500 to-teal-500 origin-left z-[60]" style={{ scaleX }} />

      <Navbar darkMode={darkMode} toggleTheme={() => setDarkMode(!darkMode)} />

      <main className="flex flex-col relative z-10">
        <Hero />
        <PainPoints />
        <About />
        <CompetitiveEdge />
        <Projects />
        <LaunchPlan />
        <Testimonials />
        <Contact />
      </main>

      <Footer />

      <AnimatePresence>
        {showFab && (
          <motion.a
            href="#contact"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0 }}
            className="fixed bottom-6 right-6 z-50 p-4 bg-indigo-600 text-white rounded-full shadow-2xl md:hidden flex items-center justify-center"
            aria-label="Get My Launch Plan"
          >
            <MessageSquare size={24} />
          </motion.a>
        )}
      </AnimatePresence>
    </div>
  );
}

