import React, { useState, useEffect } from 'react';
import { Menu, X, Moon, Sun } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { NAV_LINKS } from '../constants';

interface NavbarProps {
  darkMode: boolean;
  toggleTheme: () => void;
}

export const Navbar: React.FC<NavbarProps> = ({ darkMode, toggleTheme }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleNavClick = (e: React.MouseEvent<HTMLAnchorElement>, href: string) => {
    if (href.startsWith('#')) {
      e.preventDefault();
      if (location.pathname !== '/') {
        navigate('/' + href);
      } else {
        const targetId = href.replace('#', '');
        const element = document.getElementById(targetId);
        if (element) {
          const offset = 100;
          window.scrollTo({
            top: element.getBoundingClientRect().top + window.scrollY - offset,
            behavior: 'smooth'
          });
        }
      }
    }
    setIsOpen(false);
  };

  return (
    <motion.nav 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className={`fixed z-50 transition-all duration-300 w-full flex justify-center ${
        scrolled ? 'top-4' : 'top-0'
      }`}
    >
      <div 
        className={`${
          scrolled 
            ? 'w-[95%] md:w-[90%] max-w-6xl rounded-full bg-white/70 dark:bg-slate-900/60 backdrop-blur-xl border border-slate-200/50 dark:border-slate-700/50 shadow-lg' 
            : 'w-full bg-transparent border-transparent'
        } transition-all duration-500 px-6 py-4`}
      >
        <div className="flex items-center justify-between">
          <Link to="/" className="flex-shrink-0">
            <span className="text-2xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-500 via-purple-500 to-teal-400">
              Zarar.
            </span>
          </Link>

          <div className="hidden md:flex items-center space-x-6">
            {NAV_LINKS.map((link) => (
              <a
                key={link.name}
                href={link.href}
                onClick={(e) => handleNavClick(e, link.href)}
                className="text-xs font-bold text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors tracking-widest uppercase"
              >
                {link.name}
              </a>
            ))}

            <Link to="/about" className={`text-xs font-bold tracking-widest uppercase ${location.pathname === '/about' ? 'text-indigo-500' : 'text-slate-600 dark:text-slate-300'}`}>
              About
            </Link>
            <Link to="/stories" className={`text-xs font-bold tracking-widest uppercase ${location.pathname === '/stories' ? 'text-indigo-500' : 'text-slate-600 dark:text-slate-300'}`}>
              Stories
            </Link>

            <div className="flex items-center gap-4 pl-4 border-l border-slate-200 dark:border-slate-700">
              <button
                onClick={toggleTheme}
                className="p-2 rounded-full bg-slate-100 dark:bg-slate-800/80 hover:bg-indigo-100 dark:hover:bg-indigo-900/30 transition-colors text-slate-600 dark:text-slate-300"
              >
                {darkMode ? <Sun size={18} /> : <Moon size={18} />}
              </button>
              <Link 
                to="/contact"
                className="px-6 py-2.5 rounded-full bg-slate-900 dark:bg-white text-white dark:text-slate-900 text-xs font-bold uppercase tracking-wider hover:shadow-xl transition-all"
              >
                Get Started
              </Link>
            </div>
          </div>

          <div className="md:hidden flex items-center gap-4">
            <button onClick={() => setIsOpen(!isOpen)} className="p-2 text-slate-600 dark:text-slate-300">
              {isOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        <AnimatePresence>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="md:hidden mt-4 border-t border-slate-200 dark:border-slate-800 pt-4"
            >
              <div className="flex flex-col space-y-4 pb-4">
                {NAV_LINKS.map((link) => (
                  <a key={link.name} href={link.href} onClick={(e) => handleNavClick(e, link.href)} className="text-sm font-bold uppercase tracking-widest">
                    {link.name}
                  </a>
                ))}
                <Link to="/about" onClick={() => setIsOpen(false)} className="text-sm font-bold uppercase tracking-widest">About</Link>
                <Link to="/stories" onClick={() => setIsOpen(false)} className="text-sm font-bold uppercase tracking-widest">Stories</Link>
                <Link to="/contact" onClick={() => setIsOpen(false)} className="block w-full text-center py-3 bg-indigo-600 text-white font-bold rounded-xl mt-4">
                  Contact
                </Link>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.nav>
  );
};

