import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { motion, useScroll, useSpring, AnimatePresence } from 'framer-motion';
import { Navbar } from './components/Navbar.tsx';
import { Footer } from './components/Footer.tsx';
import { MessageSquare } from 'lucide-react';

// Pages
import { Home } from './pages/Home.tsx';
import { AboutPage } from './pages/AboutPage.tsx';
import { SuccessStoriesPage } from './pages/SuccessStoriesPage.tsx';
import { ContactPage } from './pages/ContactPage.tsx';

const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
};

export default function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showFab, setShowFab] = useState(false);
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, { stiffness: 100, damping: 30 });

  useEffect(() => {
    setDarkMode(true); 
    const handleScroll = () => setShowFab(window.scrollY > 500);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <Router>
      <ScrollToTop />
      <div className="relative min-h-screen overflow-x-hidden bg-slate-50 dark:bg-[#030014]">
        {/* Persistent Background Effects */}
        <div className="fixed inset-0 opacity-[0.03] pointer-events-none z-[100] mix-blend-overlay bg-[url('https://grainy-gradients.vercel.app/noise.svg')]"></div>
        <motion.div className="fixed top-0 left-0 right-0 h-1 bg-gradient-to-r from-indigo-500 via-purple-500 to-teal-500 origin-left z-[60]" style={{ scaleX }} />

        <Navbar darkMode={darkMode} toggleTheme={() => setDarkMode(!darkMode)} />

        <main className="relative z-10 pt-20">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/stories" element={<SuccessStoriesPage />} />
            <Route path="/contact" element={<ContactPage />} />
          </Routes>
        </main>

        <Footer />

        <AnimatePresence>
          {showFab && (
            <motion.a
              href="#contact"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0 }}
              className="fixed bottom-6 right-6 z-50 p-4 bg-indigo-600 text-white rounded-full shadow-2xl md:hidden flex items-center justify-center"
              aria-label="Get My Launch Plan"
            >
              <MessageSquare size={24} />
            </motion.a>
          )}
        </AnimatePresence>
      </div>
    </Router>
  );
}

import React, { useRef } from 'react';
import { motion, useMotionTemplate, useMotionValue, useSpring, useTransform } from 'framer-motion';
import { Github, ExternalLink } from 'lucide-react';
import { Project } from '../types';

interface ProjectCardProps {
  project: Project;
  index: number;
}

export const ProjectCard: React.FC<ProjectCardProps> = ({ project, index }) => {
  const ref = useRef<HTMLDivElement>(null);

  // 3D Tilt Effect Logic
  const x = useMotionValue(0);
  const y = useMotionValue(0);

  const mouseX = useSpring(x, { stiffness: 500, damping: 100 });
  const mouseY = useSpring(y, { stiffness: 500, damping: 100 });

  // Calculate rotations using useTransform hooks
  const rotateX = useTransform(mouseY, (val) => val / 20);
  const rotateY = useTransform(mouseX, (val) => -val / 20);

  // Create the transform template at the top level
  const transform = useMotionTemplate`perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;

  function onMouseMove({ currentTarget, clientX, clientY }: React.MouseEvent) {
    const { left, top, width, height } = currentTarget.getBoundingClientRect();
    x.set(clientX - left - width / 2);
    y.set(clientY - top - height / 2);
  }

  function onMouseLeave() {
    x.set(0);
    y.set(0);
  }

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
      onMouseMove={onMouseMove}
      onMouseLeave={onMouseLeave}
      className="group relative h-full"
    >
      <motion.div 
        className="h-full bg-white dark:bg-[#0f1115] rounded-3xl overflow-hidden border border-slate-200 dark:border-slate-800 shadow-lg group-hover:shadow-2xl group-hover:shadow-indigo-500/20 transition-all duration-500 flex flex-col"
        style={{ transform }}
      >
        <div className="relative overflow-hidden aspect-[16/10]">
          {/* Overlay */}
          <div className="absolute inset-0 bg-slate-900/60 transition-opacity duration-300 z-10 opacity-0 group-hover:opacity-100 backdrop-blur-[2px] flex items-center justify-center">
             <div className="flex gap-4 translate-y-4 group-hover:translate-y-0 transition-transform duration-300">
               <a 
                href={project.links.demo} 
                className="p-3 bg-white text-slate-900 rounded-full hover:bg-indigo-500 hover:text-white transition-colors shadow-lg transform hover:scale-110"
                title="View Live Demo"
               >
                 <ExternalLink size={20} />
               </a>
               <a 
                href={project.links.github} 
                className="p-3 bg-white text-slate-900 rounded-full hover:bg-indigo-500 hover:text-white transition-colors shadow-lg transform hover:scale-110"
                title="View Source Code"
               >
                 <Github size={20} />
               </a>
             </div>
          </div>

          <img 
            src={project.image} 
            alt={project.title} 
            className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-700"
          />
        </div>

        <div className="p-8 flex-1 flex flex-col relative bg-white dark:bg-[#0f1115]">
          <h3 className="text-2xl font-bold text-slate-900 dark:text-white mb-3 group-hover:text-indigo-500 transition-colors flex items-center gap-2">
            {project.title}
          </h3>

          <p className="text-slate-600 dark:text-slate-400 mb-6 text-sm flex-1 leading-relaxed">
            {project.description}
          </p>

          <div className="flex flex-wrap gap-2 mt-auto">
            {project.tags.map(tag => (
              <span 
                key={tag} 
                className="px-3 py-1 text-xs font-semibold bg-slate-100 dark:bg-slate-800 text-slate-600 dark:text-slate-300 rounded-full border border-slate-200 dark:border-slate-700 group-hover:border-indigo-500/30 transition-colors"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

