import React, { useState, useEffect } from 'react';
import { Menu, X, Moon, Sun } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { NAV_LINKS } from '../constants';

interface NavbarProps {
  darkMode: boolean;
  toggleTheme: () => void;
}

export const Navbar: React.FC<NavbarProps> = ({ darkMode, toggleTheme }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleNavClick = (e: React.MouseEvent<HTMLAnchorElement>, href: string) => {
    if (href.startsWith('#')) {
      e.preventDefault();
      if (location.pathname !== '/') {
        // If we're not on home, navigate home with hash
        navigate('/' + href);
      } else {
        // Smooth scroll if on home
        const targetId = href.replace('#', '');
        const element = document.getElementById(targetId);
        if (element) {
          const offset = 100;
          const bodyRect = document.body.getBoundingClientRect().top;
          const elementRect = element.getBoundingClientRect().top;
          const elementPosition = elementRect - bodyRect;
          const offsetPosition = elementPosition - offset;

          window.scrollTo({
            top: offsetPosition,
            behavior: 'smooth'
          });
        }
      }
    }
    setIsOpen(false);
  };

  return (
    <motion.nav 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className={`fixed z-50 transition-all duration-300 w-full flex justify-center ${
        scrolled ? 'top-4' : 'top-0'
      }`}
    >
      <div 
        className={`${
          scrolled 
            ? 'w-[95%] md:w-[90%] max-w-6xl rounded-full bg-white/70 dark:bg-slate-900/60 backdrop-blur-xl border border-slate-200/50 dark:border-slate-700/50 shadow-lg' 
            : 'w-full bg-transparent border-transparent'
        } transition-all duration-500 px-6 py-4`}
      >
        <div className="flex items-center justify-between">
          {/* Logo */}
          <Link to="/" className="flex-shrink-0 cursor-pointer">
            <span className="text-2xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-500 via-purple-500 to-teal-400">
              Zarar.
            </span>
          </Link>

          {/* Desktop Menu */}
          <div className="hidden md:flex items-center space-x-6">
            {NAV_LINKS.map((link) => (
              <a
                key={link.name}
                href={link.href}
                onClick={(e) => handleNavClick(e, link.href)}
                className="text-xs font-bold text-slate-600 dark:text-slate-300 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors tracking-widest uppercase relative group"
              >
                {link.name}
              </a>
            ))}

            <Link to="/about" className={`text-xs font-bold tracking-widest uppercase ${location.pathname === '/about' ? 'text-indigo-500' : 'text-slate-600 dark:text-slate-300'}`}>
              About
            </Link>
            <Link to="/stories" className={`text-xs font-bold tracking-widest uppercase ${location.pathname === '/stories' ? 'text-indigo-500' : 'text-slate-600 dark:text-slate-300'}`}>
              Stories
            </Link>

            <div className="flex items-center gap-4 pl-4 border-l border-slate-200 dark:border-slate-700">
              <button
                onClick={toggleTheme}
                className="p-2 rounded-full bg-slate-100 dark:bg-slate-800/80 hover:bg-indigo-100 dark:hover:bg-indigo-900/30 transition-colors text-slate-600 dark:text-slate-300"
              >
                {darkMode ? <Sun size={18} /> : <Moon size={18} />}
              </button>
              <Link 
                to="/contact"
                className="px-6 py-2.5 rounded-full bg-slate-900 dark:bg-white text-white dark:text-slate-900 text-xs font-bold uppercase tracking-wider hover:shadow-xl transition-all"
              >
                Get Started
              </Link>
            </div>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center gap-4">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="p-2 text-slate-600 dark:text-slate-300"
            >
              {isOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        <AnimatePresence>
          {isOpen && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="md:hidden mt-4 border-t border-slate-200 dark:border-slate-800 pt-4"
            >
              <div className="flex flex-col space-y-4 pb-4">
                {NAV_LINKS.map((link) => (
                  <a
                    key={link.name}
                    href={link.href}
                    onClick={(e) => handleNavClick(e, link.href)}
                    className="text-sm font-bold uppercase tracking-widest text-slate-600 dark:text-slate-300"
                  >
                    {link.name}
                  </a>
                ))}
                <Link to="/about" onClick={() => setIsOpen(false)} className="text-sm font-bold uppercase tracking-widest">About</Link>
                <Link to="/stories" onClick={() => setIsOpen(false)} className="text-sm font-bold uppercase tracking-widest">Stories</Link>
                <Link 
                  to="/contact"
                  onClick={() => setIsOpen(false)}
                  className="block w-full text-center py-3 bg-indigo-600 text-white font-bold rounded-xl mt-4"
                >
                  Contact
                </Link>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.nav>
  );
};

import React, { useRef } from 'react';
import { motion, useMotionTemplate, useMotionValue, useSpring, useTransform } from 'framer-motion';
import { Github, ExternalLink } from 'lucide-react';
import { Project } from '../types';

interface ProjectCardProps {
  project: Project;
  index: number;
}

export const ProjectCard: React.FC<ProjectCardProps> = ({ project, index }) => {
  const ref = useRef<HTMLDivElement>(null);

  // 3D Tilt Effect Logic
  const x = useMotionValue(0);
  const y = useMotionValue(0);

  const mouseX = useSpring(x, { stiffness: 500, damping: 100 });
  const mouseY = useSpring(y, { stiffness: 500, damping: 100 });

  // Use useTransform to map mouse position to rotation values
  const rotateX = useTransform(mouseY, (val) => val / 20);
  const rotateY = useTransform(mouseX, (val) => -val / 20);

  const transform = useMotionTemplate`perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;

  function onMouseMove({ currentTarget, clientX, clientY }: React.MouseEvent) {
    const { left, top, width, height } = currentTarget.getBoundingClientRect();
    x.set(clientX - left - width / 2);
    y.set(clientY - top - height / 2);
  }

  function onMouseLeave() {
    x.set(0);
    y.set(0);
  }

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
      onMouseMove={onMouseMove}
      onMouseLeave={onMouseLeave}
      className="group relative h-full"
    >
      <motion.div 
        className="h-full bg-white dark:bg-[#0f1115] rounded-3xl overflow-hidden border border-slate-200 dark:border-slate-800 shadow-lg group-hover:shadow-2xl group-hover:shadow-indigo-500/20 transition-all duration-500 flex flex-col"
        style={{ transform }}
      >
        <div className="relative overflow-hidden aspect-[16/10]">
          {/* Overlay */}
          <div className="absolute inset-0 bg-slate-900/60 transition-opacity duration-300 z-10 opacity-0 group-hover:opacity-100 backdrop-blur-[2px] flex items-center justify-center">
             <div className="flex gap-4 translate-y-4 group-hover:translate-y-0 transition-transform duration-300">
               <a 
                href={project.links.demo} 
                className="p-3 bg-white text-slate-900 rounded-full hover:bg-indigo-500 hover:text-white transition-colors shadow-lg transform hover:scale-110"
                title="View Live Demo"
               >
                 <ExternalLink size={20} />
               </a>
               <a 
                href={project.links.github} 
                className="p-3 bg-white text-slate-900 rounded-full hover:bg-indigo-500 hover:text-white transition-colors shadow-lg transform hover:scale-110"
                title="View Source Code"
               >
                 <Github size={20} />
               </a>
             </div>
          </div>

          <img 
            src={project.image} 
            alt={project.title} 
            className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-700"
          />
        </div>

        <div className="p-8 flex-1 flex flex-col relative bg-white dark:bg-[#0f1115]">
          <h3 className="text-2xl font-bold text-slate-900 dark:text-white mb-3 group-hover:text-indigo-500 transition-colors flex items-center gap-2">
            {project.title}
          </h3>

          <p className="text-slate-600 dark:text-slate-400 mb-6 text-sm flex-1 leading-relaxed">
            {project.description}
          </p>

          <div className="flex flex-wrap gap-2 mt-auto">
            {project.tags.map(tag => (
              <span 
                key={tag} 
                className="px-3 py-1 text-xs font-semibold bg-slate-100 dark:bg-slate-800 text-slate-600 dark:text-slate-300 rounded-full border border-slate-200 dark:border-slate-700 group-hover:border-indigo-500/30 transition-colors"
              >
                {tag}
              </span>
            ))}
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

